

WITH total_docid AS (     SELECT COUNT(DISTINCT artifactid)  as doc_count FROM artifact_abstracts where abstract is not null) select docid,term,(tf*(log_10(((SELECT max(doc_count) FROM total_docid)*1.0)/(1.0+jcount))+1.0)) as tfidf FROM (unindexed select * from JGROUPORDERED('query: select  term,docid,(1.0*count(*))/(1.0* sum(count(*)) OVER (PARTITION BY docid)) AS tf from(          select docid, strsplitv(abstract) as term from(             select  artifactid as docid, stem(filterstopwords(keywords(lowerize(abstract)))) as abstract from artifact_abstracts where abstract is not null  )   where abstract is not null    )     group by term,docid','term','docid') );
